<?phpinclude_once('../../tripko-backend/config/Database.php');include_once('../../tripko-backend/models/TouristSpot.php');?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Tourist Spots - Tripko</title>    <link href="../file_css/styles.css" rel="stylesheet">    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css">    <style>        .card {            transition: transform 0.3s ease, box-shadow 0.3s ease;        }        .card:hover {            transform: translateY(-5px);            box-shadow: 0 10px 20px rgba(0,0,0,0.1);        }        .category-tag {            background-color: #255D8A;            color: white;            padding: 4px 12px;            border-radius: 999px;            font-size: 0.75rem;            font-weight: 600;        }    </style></head><body class="bg-gray-50">    <nav class="bg-white shadow-md">        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">            <div class="flex justify-between h-16">                <div class="flex items-center">                    <a href="homepage.php" class="flex items-center">                        <i class="fas fa-compass text-2xl text-[#255D8A]"></i>                        <span class="ml-2 text-xl font-semibold text-gray-900">Tripko</span>                    </a>                </div>                <div class="flex items-center space-x-4">                    <a href="homepage.php" class="text-gray-600 hover:text-[#255D8A]">Home</a>                    <a href="#" class="text-gray-600 hover:text-[#255D8A]">About</a>                    <a href="#" class="text-gray-600 hover:text-[#255D8A]">Contact</a>                </div>            </div>        </div>    </nav>    <main class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">        <div class="mb-8">            <h1 class="text-3xl font-bold text-gray-900 mb-4">Tourist Spots</h1>            <div class="relative">                <input type="text"                        id="searchInput"                        placeholder="Search tourist spots..."                        class="w-full px-4 py-2 pl-10 pr-4 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#255D8A] focus:border-transparent"                       value="<?php echo isset($_GET['search']) ? htmlspecialchars($_GET['search']) : ''; ?>">                <i class="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"></i>            </div>        </div>        <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" id="spotsGrid">            <?php            $database = new Database();            $db = $database->getConnection();            $spot = new TouristSpot($db);            if (isset($_GET['search'])) {                $result = $spot->search($_GET['search']);            } else {                $result = $spot->read();            }            if ($result) {                while ($row = $result->fetch_assoc()) {                    echo '<div class="card bg-white rounded-lg overflow-hidden shadow-md">';                    echo '<div class="relative h-48 overflow-hidden">';                    echo '<img src="/TripKo-System/uploads/' . htmlspecialchars($row['image_path']) . '"                                alt="' . htmlspecialchars($row['name']) . '"                               class="w-full h-full object-cover"                               onerror="this.src=\'../images/placeholder.jpg\'">';                    echo '</div>';                    echo '<div class="p-4">';                    echo '<div class="flex justify-between items-start mb-2">';                    echo '<h3 class="text-xl font-semibold text-gray-900">' . htmlspecialchars($row['name']) . '</h3>';                    echo '<span class="category-tag">' . htmlspecialchars($row['category']) . '</span>';                    echo '</div>';                    echo '<p class="text-gray-600 text-sm mb-3">' . htmlspecialchars($row['town_name']) . '</p>';                    echo '<p class="text-gray-700 mb-4 line-clamp-2">' . htmlspecialchars($row['description']) . '</p>';                    echo '<a href="tourist_spot_details.php?id=' . $row['spot_id'] . '"                             class="inline-block px-4 py-2 bg-[#255D8A] text-white rounded hover:bg-[#1e4d70] transition-colors">                            View Details                         </a>';                    echo '</div>';                    echo '</div>';                }            } else {                echo '<p class="text-gray-600 text-center">No tourist spots found.</p>';            }            ?>        </div>    </main>    <script>        document.getElementById('searchInput').addEventListener('input', function(e) {            const searchQuery = e.target.value.trim();            if (searchQuery.length > 0) {                window.location.search = `search=${encodeURIComponent(searchQuery)}`;            } else if (window.location.search) {                window.location.search = '';            }        });    </script></body></html>